position: relative;
  display: inline-block;
  color: var(--ch5-animation--color);

  /// Applied when startAnimating='true'
  .ch5-animation--start-animating-true {
    animation-play-state: running;
  }

  /// Applied when startAnimating='false'
  .ch5-animation--start-animating-false {
    animation-play-state: paused;
  }

position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  z-index: -1;

  /// @require .ch5-background
  /// Added automatically on the first parent of the ch5-background element
  &--parent {
    position: relative;
    z-index: 1;
  }

  /// @require .ch5-background
  /// Added automatically on the all canvas child of ch5-background element
  &--canvas {
    position: absolute;
    left: 0;
    top: 0;
    opacity: 0;
    transition-property: opacity;
    transition-duration: var(--ch5-background--transition-duration);
    transition-timing-function: var(--ch5-background--transition-timing-function);

    &.ch5bg-fadein {
      opacity: 1;
    }
  }

position: absolute;
